    "class": "Device",
    "async": true,
    "label": "my BIG-IP declaration for declarative onboarding",
    "Common": {
        "class": "Tenant",
        "hostname": "{{bigip1_hostname}}",
        "myDns": {
            "class": "DNS",
            "nameServers": [
                "{{dns_server}}",
                "2001:4860:4860::8844"
            ],
            "search": [
                "f5lab.dev"
            ]
        },
        "myNtp": {
            "class": "NTP",
            "servers": [
                "0.pool.ntp.org",
                "1.pool.ntp.org",
                "2.pool.ntp.org"
            ],
